INSERT INTO `sub_product1` (`sub_product_id`, `sub_product_name`, `model_no`, `category_id`, `subcategory_id`, `brand_id`, `short_description`, `features`, `specifications`, `download_link`, `file_name`, `uploaded_on`) VALUES
(1, 'DM 280 CONCRETE CORING MACHINE, 350MM, 2.7KW', '966554101', 39, 48, 2, '<ul>\r\n	<li>Drill with maximum pressure.</li>\r\n	<li>Durable and ergonomic design.</li>\r\n	<li>Reliable gearbox. Easy start with reduced speed.</li>\r\n</ul>\r\n', '<ul>\r\n	<li>Drill with maximum pressure.&nbsp;A LED indicator shows the load of the machine, allowing you to always drill at maximum pressure.</li>\r\n	<li>Durable and ergonomic design.&nbsp;Durable and shock-proof, designed to protect the electronics. Easy to carry and install thanks to low weight and ergonomic design.</li>\r\n	<li>Reliable gearbox.&nbsp;Water-cooled 3 speed gearbox for optimal drilling performance and longer service intervals.</li>\r\n	<li>Easy start with reduced speed.&nbsp;To facilitate making a starter hole, speed is reduced by half using the Smart Start button.</li>\r\n</ul>\r\n', '<ul>\r\n	<li><strong>Dimensions</strong></li>\r\n	<li>Water connector:&nbsp;<strong>G 1 / 4&Prime;</strong></li>\r\n	<li>Weight:&nbsp;<strong>13 kg</strong></li>\r\n	<li>Product size length:&nbsp;<strong>485 mm</strong></li>\r\n	<li>Product size width:&nbsp;<strong>142 mm</strong></li>\r\n	<li><strong>Drilling equipment</strong></li>\r\n	<li>Drill bit diameter with stand, max:&nbsp;<strong>350 mm</strong></li>\r\n	<li>Recommended drill bit diameter, min:&nbsp;<strong>50 mm</strong></li>\r\n	<li><strong>Motor</strong></li>\r\n	<li>Electronic product:&nbsp;<strong>Yes</strong></li>\r\n	<li>Frequency:&nbsp;<strong>50 Hz</strong></li>\r\n	<li>Voltage:&nbsp;<strong>220-240 V</strong></li>\r\n	<li>Rated input power:&nbsp;<strong>2.7 kW</strong></li>\r\n	<li><strong>Sound and noise</strong></li>\r\n	<li>Sound power level, guaranteed (LWA):&nbsp;<strong>113 dB(A)</strong></li>\r\n	<li>Sound power level, measured:&nbsp;<strong>112 dB(A)</strong></li>\r\n	<li>Sound pressure level at operators ear:&nbsp;<strong>98 dB(A)</strong></li>\r\n	<li><strong>Specifications</strong></li>\r\n	<li>Water pressure, max:&nbsp;<strong>8 bar</strong></li>\r\n	<li>Spindle thread, outer:&nbsp;<strong>1 1/4&Prime; 7 UNC</strong></li>\r\n	<li><strong>Transmission</strong></li>\r\n	<li>Number of gearbox speeds:&nbsp;<strong>3</strong></li>\r\n</ul>\r\n', '<p><a href=\"https://www.aabtools.com/wp-content/uploads/Husqvarna_966554101_-Manual_DM-280-Drill-motors.pdf\" target=\"_blank\">Husqvarna_966554101_ Manual_DM 280 Drill motors</a></p>\r\n', 'Husqvarna_966554101_-DM-280-Drill-motors-1.jpg,husqvarna-core-drill-machine-966554101-180x180.jpg,', '2022-03-22 10:55:07'),
(2, 'DS 250 CORE DRILL STANDS 250 MM', '966827301', 39, 48, 2, '<ul>\r\n	<li>Straight and accurate drilling.</li>\r\n	<li>Optimum cranking speed.</li>\r\n	<li>Angle drilling.</li>\r\n</ul>\r\n', '<ul>\r\n	<li>Straight and accurate drilling.&nbsp;The carriage has guide rollers which helps drill straight holes.</li>\r\n	<li>Optimum cranking speed.&nbsp;Carriage with single and double gearing for efficient drilling.</li>\r\n	<li>Angle drilling.&nbsp;The unit can be set for drilling at different angles.</li>\r\n	<li>Vertical drilling.&nbsp;Drilling upwards and downwards.</li>\r\n</ul>\r\n', '<ul>\r\n	<li><strong>Drilling equipment</strong></li>\r\n	<li>Drill bit diameter, max:&nbsp;<strong>250 mm</strong></li>\r\n	<li>Drill bit length with recommended drill motor, max:&nbsp;<strong>584/700 mm</strong></li>\r\n	<li><strong>Specifications</strong></li>\r\n	<li>Column&rsquo;s angling:&nbsp;<strong>0-60&ordm;</strong></li>\r\n	<li><strong>Dimensions</strong></li>\r\n	<li>Travel length, max:&nbsp;<strong>685 mm</strong></li>\r\n	<li>Product size length:&nbsp;<strong>409 mm</strong></li>\r\n	<li>Product size width:&nbsp;<strong>266 mm</strong></li>\r\n	<li>Product size height:<strong>1045 mm</strong></li>\r\n	<li>Weight:&nbsp;<strong>16 kg</strong></li>\r\n</ul>\r\n', '<p><a href=\"https://www.aabtools.com/wp-content/uploads/Husqvarna_966827301_Manual_DS-250-Drill-stands.pdf\" target=\"_blank\">Husqvarna_966827301_Manual_DS 250 Drill stands</a></p>\r\n', 'Husqvarna_966827301_DS-250-Drill-stands-1.jpeg,Husqvarna_966827301_DS-250-Drill-stands-180x180.jpg,', '2022-03-22 10:57:08'),
(3, 'DS 250 CORE DRILL STANDS 250 MM', '966827301', 39, 48, 2, '<ul>\r\n	<li>Straight and accurate drilling.</li>\r\n	<li>Optimum cranking speed.</li>\r\n	<li>Angle drilling.</li>\r\n</ul>\r\n', '<ul>\r\n	<li>Straight and accurate drilling.&nbsp;The carriage has guide rollers which helps drill straight holes.</li>\r\n	<li>Optimum cranking speed.&nbsp;Carriage with single and double gearing for efficient drilling.</li>\r\n	<li>Angle drilling.&nbsp;The unit can be set for drilling at different angles.</li>\r\n	<li>Vertical drilling.&nbsp;Drilling upwards and downwards.</li>\r\n</ul>\r\n', '<ul>\r\n	<li><strong>Drilling equipment</strong></li>\r\n	<li>Drill bit diameter, max:&nbsp;<strong>250 mm</strong></li>\r\n	<li>Drill bit length with recommended drill motor, max:&nbsp;<strong>584/700 mm</strong></li>\r\n	<li><strong>Specifications</strong></li>\r\n	<li>Column&rsquo;s angling:&nbsp;<strong>0-60&ordm;</strong></li>\r\n	<li><strong>Dimensions</strong></li>\r\n	<li>Travel length, max:&nbsp;<strong>685 mm</strong></li>\r\n	<li>Product size length:&nbsp;<strong>409 mm</strong></li>\r\n	<li>Product size width:&nbsp;<strong>266 mm</strong></li>\r\n	<li>Product size height:<strong>1045 mm</strong></li>\r\n	<li>Weight:&nbsp;<strong>16 kg</strong></li>\r\n</ul>\r\n', '<p><a href=\"https://www.aabtools.com/wp-content/uploads/Husqvarna_966827301_Manual_DS-250-Drill-stands.pdf\" target=\"_blank\">Husqvarna_966827301_Manual_DS 250 Drill stands</a></p>\r\n', 'Husqvarna_966827301_DS-250-Drill-stands-1.jpeg,Husqvarna_966827301_DS-250-Drill-stands-180x180.jpg,', '2022-03-22 10:57:27'),
(4, 'DM 430 CONCRETE CORING MACHINE, 450MM, 3.2KW', '970445607', 39, 48, 2, '<ul>\r\n	<li>Sturdy and durable</li>\r\n	<li>Ergonomic</li>\r\n	<li>Optimized RPMs</li>\r\n	<li>Embedded Connectivity</li>\r\n	<li>Reliable gearbox</li>\r\n</ul>\r\n', '<ul>\r\n	<li>Equipped with an aluminum housing and the protective carrying handle ensures rough handling.</li>\r\n	<li>Equipped with a carrying handle for easy transport and handling.</li>\r\n	<li>Optimized RPMs. Enables high drilling performance.</li>\r\n	<li>An enhanced version of Husqvarna Fleet Services&trade;. Enables data analytics and insights into the health of the machine.</li>\r\n	<li>The LED indicator shows the load of the machine, allowing the operator to always drill at maximum pressure, for best performance.</li>\r\n	<li>To facilitate making a starter hole, speed is reduced by half using the Smart Start&reg; button.</li>\r\n	<li>The machine uses pulsation to warn you of an approaching overload. This increases productivity and extends product life.</li>\r\n	<li>3-speed gearbox for optimal drilling performance and longer service intervals.</li>\r\n</ul>\r\n', '<ul>\r\n	<li>Power Source: Electric</li>\r\n	<li>Power Voltage: 230V</li>\r\n	<li>Frequency: 50-60 Hz</li>\r\n	<li>Output Power: 3.2 kW</li>\r\n	<li>Drill bit Diameter: 100 mm</li>\r\n	<li>Drill bit Length: 450 mm</li>\r\n	<li>Max. Water Pressure: 3 Bar</li>\r\n	<li>Spindle Thread: 11 4/7&Prime; UNC</li>\r\n	<li>No. of Gearbox Speeds: 3</li>\r\n	<li>Sound Power Level: 110 dB(A)</li>\r\n	<li>Sound Pressure Level: 92 dB(A)</li>\r\n	<li>Weight: 15.5 kg</li>\r\n	<li>Water connector: G 1 / 4&Prime;</li>\r\n</ul>\r\n', '<p><a href=\"https://www.aabtools.com/wp-content/uploads/Husqvarna_DM-430_Drill-Motor_970445607_Manual.pdf\" target=\"_blank\">Husqvarna_DM 430_Drill Motor_970445607_Manual</a></p>\r\n', 'Untitled-1-180x180.jpg,', '2022-03-22 10:58:59'),
(5, 'DM 220 CONCRETE CORING MACHINE, 150 MM, 1.85KW', '966563501', 39, 48, 3, '<ul>\r\n	<li>360&deg; adjustable front handle</li>\r\n	<li>Guidance for exact drilling</li>\r\n	<li>Overload protection</li>\r\n	<li>Durable and ergonomic design</li>\r\n</ul>\r\n', '<ul>\r\n	<li>360&deg; adjustable front handle for best ergonomic position</li>\r\n	<li>An electronic positioning system shows when the machine is in a vertical, horizontal or preferred angled position.</li>\r\n	<li>The machine uses pulsation to warn you of an approaching overload and motor will eventually shut off, to prevent overheating.</li>\r\n	<li>Durable and shock-proof, designed to protect the electronics. Easy to carry and install thanks to low weight and ergonomic design.</li>\r\n	<li>Easy to switch between dry and wet drilling</li>\r\n	<li>Shows when the machine is in a vertical, horizontal or preferred angled position. This provides efficient drilling.</li>\r\n	<li>Drilling holes in stepless adjustable angles.</li>\r\n</ul>\r\n\r\n<h3>RELATED PRODUCTS</h3>\r\n', '<ul>\r\n	<li>Power Source: Electric</li>\r\n	<li>Power Voltage: 230V</li>\r\n	<li>Frequency: 50 Hz</li>\r\n	<li>Output Power: 1.85 kW</li>\r\n	<li>Drill bit diameter with stand, max: 150 mm</li>\r\n	<li>Drill bit diameter handheld, max: 80 mm</li>\r\n	<li>Water pressure, max: 8 Bar</li>\r\n	<li>Spindle Thread, outer: 11 4/7&Prime; UNC</li>\r\n	<li>No. of Gearbox Speeds: 3</li>\r\n	<li>Sound power level, guaranteed (LWA): 108 dB(A)</li>\r\n	<li>Sound power level, measured: 107 dB(A)</li>\r\n	<li>Sound pressure level at operators ear: 93 dB(A)</li>\r\n	<li>Weight: 7 kg</li>\r\n	<li>Water connector: G 1 / 4&Prime;</li>\r\n	<li>Product size length: 560 mm</li>\r\n	<li>Product size width: 105 mm</li>\r\n	<li>Spindle thread, inner: G 1/2&Prime;</li>\r\n</ul>\r\n', '<p><a href=\"https://www.aabtools.com/wp-content/uploads/Husqvarana-966563501-DM220-Coring-Machine.pdf\" target=\"_blank\">Husqvarana-966563501-DM220-Coring Machine</a></p>\r\n', 'Husqvarana-966563501-DM220-Coring-Machine-180x180.png,', '2022-03-22 11:00:50'),
(6, 'CORE DRILL STAND, 500 MM, 90 TILT, DS 500', '967968601', 39, 48, 2, '<ul>\r\n	<li>Durable and sturdy</li>\r\n	<li>Precision and performance</li>\r\n	<li>High stability and low weight</li>\r\n	<li>Easy to install and operate</li>\r\n</ul>\r\n', '<ul>\r\n	<li>The DS 500 is a medium-sized drill stand suitable for a wide range of professional core drilling operations.</li>\r\n	<li>It combines high stability, strength, and is surprisingly lightweight.</li>\r\n	<li>Quick connection features and low weight make handling easy and efficient.</li>\r\n	<li>The slim car with optimal transmission performs exceptionally well with the Husqvarna DM 280, DM 340 and similar high-performance single-phase drill motors.</li>\r\n	<li>Recommended equipment: DM 340, DM 400, DM 280 drilling motor</li>\r\n</ul>\r\n', '<ul>\r\n	<li>Drill Bit Diameter: 500 mm</li>\r\n	<li>Drill Bit Length: 680 mm</li>\r\n	<li>Max. Travel Length: 706 mm</li>\r\n	<li>Column Angle: 90&ordm;</li>\r\n	<li>Weight: 17.6 kg</li>\r\n	<li>Dimensions: 394 x 240 x 1050 mm</li>\r\n</ul>\r\n', '<p><a href=\"https://www.aabtools.com/wp-content/uploads/Husqvarna-DRILL-STAND-DS-500-967-96-86-01.pdf\" target=\"_blank\">Husqvarna DRILL STAND DS 500 &ndash; 967 96 86-01</a></p>\r\n', 'Husqvarna_966827301_DS-250-Drill-stands-180x180.jpg,', '2022-03-22 11:02:33'),
(7, '14-LITER, CONSTANT PRESSURE WATER TANK, 3-METER HOSE, WT2GO', '598742401', 39, 48, 2, '<ul>\r\n	<li>A compact and easy-to-use 14-liter constant pressure water tank.</li>\r\n	<li>Ideal for concrete cutting, core drilling, and grinding concrete.</li>\r\n	<li>Provides constant water flow without the use of a pump, battery, or electronics.</li>\r\n</ul>\r\n', '<ul>\r\n	<li>A compact and easy-to-use 14-liter constant pressure water tank. Ideal for concrete cutting, core drilling, and grinding concrete.</li>\r\n	<li>Provides constant water flow without the use of a pump, battery, or electronics.</li>\r\n	<li>It is designed and engineered to simplify the process of supplying water in situations where regular hosing is difficult.</li>\r\n	<li>The WT2GO includes a multi-function nozzle, an on/off valve, 3 meters of reinforced hose, all mounted on a compact two-chamber frame design</li>\r\n	<li>Fits To &mdash; K 970, K 2500, DM 220, DM 230, DM 280, DMS 240, K 6500, K 770, K 535i, K 4000, K 4000 Cut-n-Break, DM 400, K 1270, K 7000, DM 430, DM 200, DM 540i, K 7000 SmartGuard, K 1270 SmartGuard</li>\r\n</ul>\r\n', '<ul>\r\n	<li>Min. Water Pressure: 0.5 bar</li>\r\n	<li>Max. Water Pressure: 0.7 bar</li>\r\n	<li>Pressure Output: 10 Psi</li>\r\n	<li>Hose Length: 3 meters</li>\r\n	<li>Weight: 6 kg</li>\r\n	<li>Dimensions: 144 x 325 x 800 mm</li>\r\n</ul>\r\n', '<p><a href=\"https://www.aabtools.com/wp-content/uploads/Husqvarna_WT2GO_Water-Tank_598742401_Manual.pdf\" target=\"_blank\">Husqvarna_WT2GO_Water Tank_598742401_Manual</a></p>\r\n', 'husqvarna-pressure-water-tank-14-liter-598742401-180x180.jpg,', '2022-03-22 11:03:59'),
(10, '350 MM, POWER CUTTER, 2-STROKE ENGINE, 3.7 KW, K 770', '967682101', 39, 48, 2, '<ul>\r\n	<li>Easy and optimal belt tensioning.</li>\r\n	<li>Light and powerful.</li>\r\n	<li>Heavy-duty cutting arm.</li>\r\n	<li>Long service intervals.</li>\r\n</ul>\r\n', '<ul>\r\n	<li>The semi-automatic SmartTension minimizes the risk of under- and over tensioning</li>\r\n	<li>A light, compact and powerful machine with a superior power-to-weight ratio.</li>\r\n	<li>The new-designed cast magnesium crankcase has a three-bolt joint to attach the cutting head, enabling higher torsional rigidity than the previous two-bolt solution.</li>\r\n	<li>A strong Poly-V belt combined with our maintenance-free starter DuraStarter&trade; and efficient filter-system Active Air Filtration&trade; ensures long service intervals.</li>\r\n	<li>Provides a powerful spark with optimized timing, enabling easy start and efficient combustion.</li>\r\n</ul>\r\n', '<ul>\r\n	<li>Power Source: Gasoline</li>\r\n	<li>Tank Capacity: 1 Liter</li>\r\n	<li>No. of Engine Stroke: 2</li>\r\n	<li>Output Power: 3.7 kW</li>\r\n	<li>Cylinders: 1</li>\r\n	<li>Blade Diameter: 350 mm</li>\r\n	<li>Arbor Diameter: 25.4 mm</li>\r\n	<li>Max. Cutting Depth: 125 mm</li>\r\n	<li>Max. Blade Thickness: 5 mm</li>\r\n	<li>Max. Peripheral Speed: 90 m/s</li>\r\n	<li>Sound Power Level: 115 dB(A)</li>\r\n	<li>Sound Pressure Level: 101 dB(A)</li>\r\n	<li>Vibrations left/front Handle: 2.0 m/s&sup2;</li>\r\n	<li>Vibrations right/rear Handle: 2.3 m/s&sup2;</li>\r\n	<li>Weight: 10.1 kg</li>\r\n	<li>Dimensions: 605 x 225 x 415 mm</li>\r\n</ul>\r\n', '<p><a href=\"https://www.aabtools.com/wp-content/uploads/Husqvarna_967682101_Manual_K-770-Power-Cutters.pdf\" target=\"_blank\">Husqvarna_967682101_Manual_K 770 Power Cutters</a></p>\r\n', 'Husqvarna_967682101_-K-770-Power-Cutters.jpg,', '2022-03-22 11:41:09'),
(11, 'K 4000 POWER CUTTERS 350MM', '967079801', 39, 48, 3, '<ul>\r\n	<li>Constant-flow water valve.</li>\r\n	<li>Ergonomically designed rear handle.</li>\r\n	<li>Increased productivity.</li>\r\n</ul>\r\n', '<ul>\r\n	<li>Constant-flow water valve.&nbsp;The integrated regulator controls the water volume, ensuring a constant flow to the blade. It also ensures the amount of water is sufficient to bind the dust without creating an excess of water.</li>\r\n	<li>Ergonomically designed rear handle.&nbsp;Power cord inlet located underneath the handle enables ease of use and comfortable handling with hassle free operation.</li>\r\n	<li>Increased productivity.&nbsp;The new pop-up carbon brushes protects the collector from unforeseen rotor changes which increases reliability and enables longer service intervals.</li>\r\n	<li>Easy serviceability.&nbsp;The one-piece inspection lid and Torx screws make it easy to assemble and service.</li>\r\n</ul>\r\n\r\n<h3>&nbsp;</h3>\r\n', '<ul>\r\n	<li>Engine</li>\r\n	<li>Max engine/motor RPM: 19350 rpm</li>\r\n	<li>Output shaft RPM, max: 3600 rpm</li>\r\n	<li>Voltage: 220-240 V</li>\r\n	<li>Rated current: 12 A</li>\r\n	<li>Engine cooling: Fan/Air</li>\r\n	<li>Equipment</li>\r\n	<li>Arbor diameter: 25,4 mm</li>\r\n	<li>Blade diameter, max: 350 mm</li>\r\n	<li>Cutting depth, max: 125 mm</li>\r\n	<li>Peripheral speed, max: 100 m/s</li>\r\n	<li>Sound and Noise</li>\r\n	<li>Sound pressure level at operators ear: 95 dB(A)</li>\r\n	<li>Sound power level, guaranteed (LWA): 105 dB(A)</li>\r\n</ul>\r\n', '', 'Husqvarna_967079801_K-4000-Power-Cutters.jpg,Husqvarna_967079801_K-4000-Power-Cutters-1.png,', '2022-03-22 11:44:31'),
(12, '370 MM, POWER CUTTER, 2-STROKE ENGINE, 4.8 KW, K 970 RING', '967272301', 39, 48, 4, '<ul>\r\n	<li>Deep cutting</li>\r\n	<li>Sturdy and reliable</li>\r\n	<li>High power with less fuel</li>\r\n	<li>Light and powerful</li>\r\n	<li>Anti-vibration system</li>\r\n</ul>\r\n', '<ul>\r\n	<li>Irregular deep cutting.</li>\r\n	<li>Less sensitive to outer conditions and fuel variations, thanks to the cylinder design and muffler with integrated heat deflector.</li>\r\n	<li>A strong Poly-V belt combined with our maintenance-free starter DuraStarter&trade; and efficient filter-system Active Air Filtration&trade; ensures long service intervals.</li>\r\n	<li>A light, compact and powerful machine with a superior power-to-weight ratio.</li>\r\n	<li>The unique design of the carburetor, cylinder, and digital ignition system, together with the air purge and decompression valve ensure an easy start.</li>\r\n</ul>\r\n', '<ul>\r\n	<li>Power Source: Gasoline</li>\r\n	<li>Tank Capacity: 1 Liter</li>\r\n	<li>No. of Engine Stroke: 2</li>\r\n	<li>Output Power: 4.8 kW</li>\r\n	<li>Cylinders: 1</li>\r\n	<li>Blade Diameter: 370 mm</li>\r\n	<li>Max. Cutting Depth: 270 mm</li>\r\n	<li>Max. Blade Thickness: 5.6 mm</li>\r\n	<li>Max. Peripheral Speed: 55 m/s</li>\r\n	<li>Sound Power Level: 115 dB(A)</li>\r\n	<li>Sound Pressure Level: 104 dB(A)</li>\r\n	<li>Vibrations left/front Handle: 2.7 m/s&sup2;</li>\r\n	<li>Vibrations right/rear Handle: 3.4 m/s&sup2;</li>\r\n	<li>Weight: 13.8 kg</li>\r\n	<li>Dimensions: 785 x 224 x 431 mm</li>\r\n</ul>\r\n', '<p><a href=\"https://www.aabtools.com/wp-content/uploads/Husqvarna_K-970-Ring_Power-Cutters_967272301_Manual.pdf\" target=\"_blank\">Husqvarna_K 970 Ring_Power Cutters_967272301_Manual</a></p>\r\n', 'Husqvarna_K-970-Ring_967272301_Power-Cutters.png,', '2022-03-22 11:46:20'),
(13, '230 MM, POWER CUTTER, 2.7 KW, 220V, K 4000 CUT-N-BREAK', '967079701', 39, 48, 4, '<ul>\r\n	<li>Cost-efficient cutting method</li>\r\n	<li>Exceptional cutting depth</li>\r\n	<li>Perfect for flush cutting</li>\r\n	<li>Ergonomically designed rear handle</li>\r\n</ul>\r\n', '<ul>\r\n	<li>With the unique cut-n-break method you can work your way through the material step-by-step at a low tool cost.</li>\r\n	<li>The long body and double cutting blades allow you to make cuts up to 400 mm deep.</li>\r\n	<li>The adjustable blade guard allows close cutting to walls and floors.</li>\r\n	<li>The power cord inlet located underneath the handle enables ease of use and comfortable handling with hassle-free operation.</li>\r\n	<li>The new pop-up carbon brushes protect the collector from unforeseen rotor changes which increases reliability and enables longer service intervals.</li>\r\n</ul>\r\n', '<ul>\r\n	<li>Power Source: Electric</li>\r\n	<li>Rated Input Power: 2.7 kW</li>\r\n	<li>Voltage: 220-230 V</li>\r\n	<li>Frequency: 50-60 Hz</li>\r\n	<li>Blade Diameter: 230 mm</li>\r\n	<li>Max. Cutting Depth: 400 mm</li>\r\n	<li>Max. Blade Thickness: 2.5 mm</li>\r\n	<li>Max. Peripheral Speed: 60 m/s</li>\r\n	<li>Sound Power Level: 109 dB(A)</li>\r\n	<li>Sound Pressure Level: 95 dB(A)</li>\r\n	<li>Vibrations left/front Handle: 3.2 m/s&sup2;</li>\r\n	<li>Vibrations right/rear Handle: 3.4 m/s&sup2;</li>\r\n	<li>Weight: 8.4 kg</li>\r\n	<li>Dimensions: 867 x 226 x 325 mm</li>\r\n</ul>\r\n', '<p><a href=\"https://www.aabtools.com/wp-content/uploads/Husqvarna_K-4000-CutnBreak_Power-Cutters_967079701_Manual.pdf\" target=\"_blank\">Husqvarna_K 4000 CutnBreak_Power Cutters_967079701_Manual</a></p>\r\n', 'Husqvarna_K-4000-CutnBreak_967079701_Power-Cutters.png,', '2022-03-22 11:47:54'),
(14, '350 MM, POWER CUTTER, 2-STROKE ENGINE, 4.8 KW, K 970', '967348001', 39, 48, 3, '<ul>\r\n	<li>Easy and optimal belt tensioning</li>\r\n	<li>Sturdy and reliable</li>\r\n	<li>Long service intervals</li>\r\n	<li>Less slurry and low water consumption</li>\r\n	<li>High power with less fuel</li>\r\n</ul>\r\n', '<ul>\r\n	<li>The semi-automatic SmartTension minimizes the risk of under- and over-tensioning.</li>\r\n	<li>Less sensitive to outer conditions and fuel variations, thanks to the cylinder design and muffler with integrated heat deflector.</li>\r\n	<li>The unique design of the carburetor, cylinder, and digital ignition system, together with the air purge and decompression valve ensure an easy start.</li>\r\n	<li>A strong Poly-V belt combined with our maintenance-free starter DuraStarter&trade; and efficient filter-system Active Air Filtration&trade; ensures long service intervals.</li>\r\n	<li>Excellent dust control with our wet cutting kit. A progressive water valve for exact adjustment of the water volume to efficiently bind the dust and reduce slurry.</li>\r\n</ul>\r\n', '<ul>\r\n	<li>Power Source: Gasoline</li>\r\n	<li>Tank Capacity: 1 Liter</li>\r\n	<li>No. of Engine Stroke: 2</li>\r\n	<li>Output Power: 4.8 kW</li>\r\n	<li>Cylinders: 1</li>\r\n	<li>Blade Diameter: 400 mm</li>\r\n	<li>Arbor Diameter: 25.4 mm</li>\r\n	<li>Max. Cutting Depth: 155 mm</li>\r\n	<li>Max. Blade Thickness: 5 mm</li>\r\n	<li>Max. Peripheral Speed: 90 m/s</li>\r\n	<li>Sound Power Level: 115 dB(A)</li>\r\n	<li>Sound Pressure Level: 104 dB(A)</li>\r\n	<li>Vibrations left/front Handle: 3.5 m/s&sup2;</li>\r\n	<li>Vibrations right/rear Handle: 4 m/s&sup2;</li>\r\n	<li>Weight: 11.6 kg</li>\r\n	<li>Dimensions: 688 x 214 x 481 mm</li>\r\n</ul>\r\n', '<p><a href=\"https://www.aabtools.com/wp-content/uploads/Husqvarna_K-970_Power-Cutters_967348001_Manual-1.pdf\" target=\"_blank\">Husqvarna_K 970 Rescue_Power Cutters_967635601_Manual</a></p>\r\n', 'Husqvarna_K-970_967348001_Power-Cutters.png,', '2022-03-22 11:50:52'),
(15, '400 MM, POWER CUTTER, 2-STROKE ENGINE, 4.8 KW, K 970', '967348101', 39, 48, 2, '<ul>\r\n	<li>Easy and optimal belt tensioning</li>\r\n	<li>Sturdy and reliable</li>\r\n	<li>Long service intervals</li>\r\n	<li>Less slurry and low water consumption</li>\r\n	<li>High power with less fuel</li>\r\n</ul>\r\n', '<ul>\r\n	<li>The semi-automatic SmartTension minimizes the risk of under- and over-tensioning.</li>\r\n	<li>Less sensitive to outer conditions and fuel variations, thanks to the cylinder design and muffler with integrated heat deflector.</li>\r\n	<li>The unique design of the carburetor, cylinder, and digital ignition system, together with the air purge and decompression valve ensure an easy start.</li>\r\n	<li>A strong Poly-V belt combined with our maintenance-free starter DuraStarter&trade; and efficient filter-system Active Air Filtration&trade; ensures long service intervals.</li>\r\n	<li>Excellent dust control with our wet cutting kit. A progressive water valve for exact adjustment of the water volume to efficiently bind the dust and reduce slurry.</li>\r\n</ul>\r\n', '<ul>\r\n	<li>Power Source: Gasoline</li>\r\n	<li>Tank Capacity: 1 Liter</li>\r\n	<li>No. of Engine Stroke: 2</li>\r\n	<li>Output Power: 4.8 kW</li>\r\n	<li>Cylinders: 1</li>\r\n	<li>Blade Diameter: 400 mm</li>\r\n	<li>Arbor Diameter: 25.4 mm</li>\r\n	<li>Max. Cutting Depth: 155 mm</li>\r\n	<li>Max. Blade Thickness: 5 mm</li>\r\n	<li>Max. Peripheral Speed: 90 m/s</li>\r\n	<li>Sound Power Level: 115 dB(A)</li>\r\n	<li>Sound Pressure Level: 104 dB(A)</li>\r\n	<li>Vibrations left/front Handle: 3.5 m/s&sup2;</li>\r\n	<li>Vibrations right/rear Handle: 4 m/s&sup2;</li>\r\n	<li>Weight: 11.6 kg</li>\r\n	<li>Dimensions: 688 x 214 x 481 mm</li>\r\n</ul>\r\n', '<p><a href=\"https://www.aabtools.com/wp-content/uploads/Husqvarna_K-970_Power-Cutters_967348001_Manual-1.pdf\" target=\"_blank\">Husqvarna_K 970 Rescue_Power Cutters_967635601_Manual</a></p>\r\n', 'Husqvarna_967682101_-K-770-Power-Cutters.jpg,',